mapper.update:
update scuee.chat
set is_sent = 1
where classroom_id=#{chat.cid} and student_id=#{chat.sid};

mapper.select:
select scuee.chat.classroom_id,scuee.chat.student_id,scuee.chat.chat_name,scuee.chat.chat_time,scuee.chat.chat_content
from scuee.chat
where scuee.chat.classroom_id = #{cid} and scuee.chat.is_sent = 0;
