mapper.update:
update scuee.chat
set is_sent = 1
where course_id=#{chat.id} and student_id=#{chat.sid};

mapper.select:
select scuee.chat.course_id,scuee.chat.student_id,scuee.chat.chat_name,scuee.chat.chat_time,scuee.chat.chat_content
from scuee.chat
where scuee.chat.course_id = #{id} and scuee.chat.is_sent = 0;
